import React, { useState } from 'react';
import './HomePage.css';

const Homepage = () => {
    const [chatHistory, setChatHistory] = useState([]);

    const handleChatSubmit = async (event) => {
        event.preventDefault();

        const userMessage = event.target.message.value;

        const response = await fetch("/chat", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ message: userMessage })
        });

        const data = await response.json();

        setChatHistory(prevHistory => [
            ...prevHistory,
            { type: 'user', text: userMessage },
            { type: 'gpt', text: data.response }
        ]);
    };

    return (
        <div>
            <p>Hello World</p>
            <form action="/" method="post" encType="multipart/form-data">
                Upload a PDF: <input type="file" name="pdf" multiple /><br /><br />
                <input type="submit" value="Upload and Embed" />
            </form>

            <br />

            <form action="/search" method="post" id="search-form">
                Search in PDFs: <input type="text" name="query" />
                <button type="submit">Search</button>
            </form>

            <div id="chat-box">
                <div id="chat-history">
                    {chatHistory.map((chat, idx) => (
                        <p key={idx}>{chat.type === 'user' ? 'User' : 'GPT'}: {chat.text}</p>
                    ))}
                </div>
                <form onSubmit={handleChatSubmit} id="chat-form">
                    <textarea name="message" placeholder="Type your message here..."></textarea>
                    <button type="submit">Send</button>
                </form>
            </div>
        </div>
    );
};

export default Homepage;
